        public void setQuestionText(string text) {
            string[] t = text.Split(new string[] { ". " }, StringSplitOptions.None);
            QuestionLabel.Text = t[1];
        }
        public void setAnswers(string[] text)
        {
            answer1.Text = "A: "+text[0];
            answer2.Text = "B: "+text[1];
            answer3.Text = "C: "+text[2];
            answer4.Text = "D: "+text[3];
        }
        public void setActiveQuestion(int questNum)
        {
            switch(questNum)
            {
                case 1: returnBackgroundColor(); quest1.BackColor = Color.Orange; break;
                case 2: returnBackgroundColor(); quest2.BackColor = Color.Orange; break;
                case 3: returnBackgroundColor(); quest3.BackColor = Color.Orange; break;
                case 4: returnBackgroundColor(); quest4.BackColor = Color.Orange; break;
